/* Custom CSS added by Portum Theme */
.services-item strong {
	font-weight: 400;
	color: #777;
	line-height: 1.5;
	margin-top: 15px;
}

#header {
	border-bottom: 1px solid rgba(255,255,255,.1);
}

#header.stuck {
	background-color: #000;
	border-bottom: 0;
}

#constat-research {
	border-bottom: 1px solid #e1e1e1;
}

#footer-widget-area-1 .widget_media_image img {
	max-width: 200px !important;
}

#white-counters .ewf-counter__odometer, .ewf-counter__symbol {
	color: #FFF;
}

.map-info-wrapper {
	box-shadow: none;
	-webkit-box-shadow: none;
}

.ewf-portfolio-item {
box-shadow: 0 0 25px rgba(35,31,32, .2);
padding-bottom:5px;
}

.ewf-portfolio-item__details {
	padding-left:30px !important;
	padding-right:30px !important;
	color:#fff;

}

.ewf-portfolio-item {
	background:#ff3366;
}

.ewf-portfolio-item a {
	color:#fff;
	font-weight:bold;
}

.ewf-portfolio-item__overlay:after {
	background-color:#ff3366;
}


.footer-social-icons {
	margin-top: 15px;

}

.footer-social-icons a{
	margin-right: 15px;
	margin-left: 0;
}

.footer-social-icons a:last-of-type {
	margin-right: 0;
}

.footer-social-icons a i{
	font-size: 24px;
	color: #CCC;
}

/* END CSS by Portum */

.ewf-slider h1 {
	color:#FFF;
}

.ewf-slider h1 strong {
	color:#97e7eb;
}

.ewf-slider h6 {
	color:rgba(255,255,255,.8);

}

.ewf-portfolio-item {
	max-height: 290px;
	overflow: hidden;
}

/* Custom CSS added by Portum theme */
.ewf-portfolio-item {
box-shadow: 0 0 25px rgba(35,31,32, .2);
padding-bottom:5px;
}

.ewf-portfolio-item__details {
	padding-left:30px !important;
	padding-right:30px !important;
	color:#fff;

}

.ewf-portfolio-item {
	background:#ff173d;
}

.ewf-portfolio-item a {
	color:#fff;
	font-weight:bold;
}

.ewf-portfolio-item__overlay:after {
	background-color:#ff173d;
}


.ewf-btn.ewf-btn--large, 
.ewf-btn.ewf-btn--huge {
	padding: 14px 46px;
	border-radius: 0;
}


.footer-social {
	text-align: center;
}
.footer-social h5 {
	font-size: 18px;
	font-weight: 900;
}

.footer-social-icons {
	margin-top: 15px;
}

.footer-social-icons a{
	margin-right: 15px;
}
.footer-social-icons a i{
	font-size: 24px;
	color: #CCC;
}


[data-section="1"] p{
	font-weight: 100;
	font-size: 14px;
}

.services-item i {
	padding: 0;
	font-size: 24px;
	width: 24px;
	height: 24px;
}
/* END custom CSS by Portum */
